mapper.update:
 
		UPDATE
			MEMBER
		SET
			m_Pw = #{cpw}
		WHERE
			m_Id = #{id}
		AND 
			m_Pw = #{pw}

mapper.select:
	SELECT
	 	m_Pw AS pw
	FROM
		MEMBER
	WHERE
		m_Id = #{id}
	AND	
 		m_Name=#{name} 
	AND 
		m_Gender=#{gender} 
	AND
		m_Email=#{email}

	SELECT
	 	m_Id AS id
	FROM
		MEMBER
	WHERE
 		m_Name=#{name} 
	AND 
		m_Gender=#{gender} 
	AND
		m_Email=#{email}

SELECT m_Id FROM MEMBER
WHERE
	m_Id = #{userid}
