mapper.select:
SELECT * FROM (SELECT MU_NO AS no, MU_TITLE AS title, MU_ARTIST AS artist, MU_ALBUM AS album, MU_GENRE AS genre, MU_IMAGEPATH AS path, MU_STAR AS star, ROW_NUMBER() OVER(ORDER BY MU_STAR DESC) AS rno FROM MUSIC WHERE UPPER(MU_ISSHOW)='Y'
AND MU_GENRE = #{genre}
AND MU_TITLE like '%'||#{svalue}||'%' OR MU_ARTIST like '%'||#{svalue}||'%' OR MU_ALBUM like '%'||#{svalue}||'%'
AND MU_TITLE like '%'||#{svalue}||'%'
AND MU_ARTIST like '%'||#{svalue}||'%'
AND MU_ALBUM like '%'||#{svalue}||'%'
) WHERE rno BETWEEN #{start} AND #{end}

mapper.update:
UPDATE MURE
	SET
	MUB_DATE = SYSDATE,
	MUB_BODY = #{body}
WHERE
	MUB_NO = #{no}

UPDATE MEMBER
	SET
	M_SEL = #{res}
WHERE
	M_ID = #{id}

mapper.select:
SELECT * FROM (SELECT MU_NO AS no, MU_TITLE AS title, MU_ARTIST AS artist, MU_ALBUM AS album, MU_GENRE AS genre, MU_IMAGEPATH AS path, MU_STAR AS star, ROW_NUMBER() OVER(ORDER BY MU_STAR DESC) AS rno FROM MUSIC WHERE UPPER(MU_ISSHOW) = 'N'
AND MU_GENRE = #{genre}
) WHERE rno BETWEEN #{start} AND #{end}
